sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

   var get_mail_address = new GlideAjax("GetEmailAddress");
   get_mail_address.addParam("sysparm_name", "getEmail");
   get_mail_address.addParam("sysparm_userID", g_form.getValue("u_requested_for"));
   get_mail_address.getXML(populate_field);

   function populate_field(response) {
      var mail_from_script_include = response.responseXML.documentElement.getAttribute("answer");
      var field = "u_requested_for_email";
      g_form.clearValue(field);
      g_form.setValue(field, mail_from_script_include);
   }
}
